'use strict'

import React, {
	Component,
	TabBarIOS
} from 'react-native';

const iconHeros = 'data:image/png;base64,';
const iconDashboard = 'data:image/png;base64,';

import Dashboard from './dashboardView';
import Heros from './herosView';

class Tabs extends Component {
	constructor(props){
		super(props)

		this.state={
			selectedTab: 'dashboard'
		}
	}

	render(){
		return(
			<TabBarIOS style={{backgroundColor: '#fff'}}>
				<TabBarIOS.Item
					title="Dashboard"
					selected={this.state.selectedTab === 'dashboard'}
					icon={{uri: iconDashboard}}
					onPress={() => {
						this.setState({
							selectedTab: 'dashboard'
						})
					}}>
					<Dashboard navigator={this.props.navigator}/>
				</TabBarIOS.Item>

				<TabBarIOS.Item
					title="Heros"
					selected={this.state.selectedTab === 'heros'}
					icon={{uri: iconHeros}}
					onPress={() => {
						this.setState({
							selectedTab: 'heros'
						})
					}}>
					<Heros/>
				</TabBarIOS.Item>
			</TabBarIOS>
		)
	}
}

module.exports = Tabs;